import React from "react";
import { Link } from "react-router-dom";

import Avatar from "../../shared/Avatar";

function Fonctionalites() {
  return (
    <React.Fragment>
      <h3>Fonctionalités: </h3>
      <hr></hr>
      <ul>
        <li>Afficher la liste de cours disponible cette session.</li>
        <li>Ajouter un cours à l'aide d'un formulaire.</li>
        <li>Afficher la liste de professeurs qui enseigne.</li>
        <li>Ajouter un professeur qui enseigne au collège.</li>
        <li>Un menu de navigation pour ce déplacer entre les pages.</li>
      </ul>
    </React.Fragment>
  );
}

export default Fonctionalites;
