import React from "react";
import { Link } from "react-router-dom";

import Avatar from "../../shared/Avatar";
import Card from "../../shared/Card";

import "./Prof.css";

function Prof({ prof }) {
  return (
    <li className="prof-item">
      <Card className="prof-item__content">
        <Link to={`/${prof.id}/places`}>
          <div className="prof-item__image">
            <Avatar image={prof.image} alt={prof.nom} />
          </div>

          <div className="prof-item__info">
            <h2>{prof.nom}</h2>
            <h3>
              {prof.places} {prof.places < 2 ? "place" : "places"}
            </h3>
          </div>
        </Link>
      </Card>
    </li>
  );
}

export default Prof;
